  data() {
    return {
      socket: null,
      responseMessage: '',
      selectedRoom: ''
    };
  },
  mounted() {
    this.initializeSocket();
  },
  methods: {
    initializeSocket() {
      this.socket = io('http://192.168.147.26:5001/liveVideo', {
        transports: ['websocket', 'polling'],
        withCredentials: true
      });
      this.socket.on('connect', () => {
        console.log('Connected to server');
