import io from 'socket.io-client';
export default {
  name: 'VideoMonitor',
  data() {
    return {
      socket: null,
      responseMessage: ''
    };
  },
  mounted() {
    this.initializeSocket();
  },
  methods: {
    initializeSocket() {
      console.log('Initializing socket connection...');
      this.socket = io('http://xiao.free.idcfengye.com', {
        transports: ['websocket', 'polling'],
        withCredentials: true
      });
      this.socket.on('connect', () => {
        console.log('Connected to server');
      });
      this.socket.on('video_frame', data => {
        console.log('Received video frame:', data);
        this.displayVideoFrame(data);
      });
      this.socket.on('disconnect', () => {
        console.log('Disconnected from server');
      });
      this.socket.on('error', error => {
        console.error('Socket error:', error);
      });
    },
    displayVideoFrame(data) {
      const canvas = document.getElementById('video-canvas');
      if (!canvas) {
        console.warn('Canvas element not found');
        return;
      }
      const ctx = canvas.getContext('2d');
      if (!ctx) {
        console.error('Failed to get 2D context for canvas:', canvas);
        return;
      }
      const blob = new Blob([new Uint8Array(data.image)], {
        type: 'image/jpeg'
      });
      const imageUrl = URL.createObjectURL(blob);
      const image = new Image();
      image.onload = () => {
        console.log('Image loaded');
        ctx.clearRect(0, 0, canvas.width, canvas.height);
        ctx.drawImage(image, 0, 0, canvas.width, canvas.height);
        URL.revokeObjectURL(imageUrl);
      };
      image.onerror = () => {
        console.error('Failed to load image');
      };
      image.src = imageUrl;
    }
  },
  beforeDestroy() {
    // Disconnect socket before component is destroyed
    if (this.socket) {
      this.socket.disconnect();
    }
  }
};
