import "core-js/modules/es.error.cause.js";
import axios from 'axios';
export default {
  data() {
    var validatePass = (rules, value, callback) => {
      if (value === '') {
        callback(new Error('请输入密码'));
      } else if (!/^(?=.*[A-Za-z])(?=.*\d)[A-Za-z\d]{8,}$/.test(value)) {
        callback(new Error('密码至少8个字符，包含字母和数字'));
      } else {
        if (this.ruleForm.checkPass !== '') {
          this.$refs.ruleForm.validateField('checkPass');
        }
        callback();
      }
    };
    var validatePass2 = (rules, value, callback) => {
      if (value === '') {
        callback(new Error('请再次输入密码'));
      } else if (value !== this.ruleForm.pass) {
        callback(new Error('两次输入密码不一致!'));
      } else {
        callback();
      }
    };
    return {
      ruleForm: {
        username: '',
        pass: '',
        checkPass: ''
      },
      rules: {
        pass: [{
          validator: validatePass,
          trigger: 'blur'
        }],
        checkPass: [{
          validator: validatePass2,
          trigger: 'blur'
        }]
      }
    };
  },
  mounted() {
    const userInfo = JSON.parse(localStorage.getItem('userInfo'));
    if (userInfo) {
      this.ruleForm.username = userInfo.username;
    }
  },
  methods: {
    submitForm(formName) {
      this.$refs[formName].validate(valid => {
        if (valid) {
          const updateData = {
            id: parseInt(this.ruleForm.username, 10),
            // Assuming username stores id, update as needed
            new_password: this.ruleForm.pass
          };
          axios.put('http://xiao.free.idcfengye.com/api/admin/update_password', updateData, {
            headers: {
              'Content-Type': 'application/json'
            }
          }).then(res => {
            this.$message.success('密码修改成功');
          }).catch(error => {
            console.error(error);
            this.$message.error('密码修改失败');
          });
        } else {
          console.log('error submit!!');
          return false;
        }
      });
    },
    resetForm(formName) {
      this.$refs[formName].resetFields();
    }
  }
};
