import Home from '../views/common/Home.vue'
import router from "@/router/index";

const routes = [
    {
        path: '/login',
        name: 'Login',
        component: () => import('../views/login/Login.vue'),
        meta: {auth: false}
    },
    {
        path: '/register',
        name: 'Register',
        component: () => import('../views/login/Register.vue'),
        meta: {auth: false}
    },
    {
        path: '/',
        name: 'home',
        component: Home,
        children: [
            {
                path: "",
                redirect: "/login",
                meta: {auth: false}
            },
            {
                // 老人列表
                path: '/oldMen',
                component: () => import('../views/page/old/oldMan.vue'),
                meta: { auth: true,title: '老人列表'}
            },
            {

                path: '/oldMenage',
                component: () => import('../views/page/old/AgeDistribution.vue'),
                meta: { auth: true,title: '老人年龄段分布'}
            },

            {
                // 老人统计分析图
                path:'/oldImage',
                component:() =>import('../views/page/old/oldImage.vue'),
                meta:{auth: true,title:'老人统计分析图'}
            },

            {
                //查看管理员信息
                path:'/managerInfo',
                component:() =>import('../views/page/manager/managerInfo.vue'),
                meta: {auth: true, title: '查看管理员信息' }

            },
            {
                //修改密码
                path:'/changePwd',
                component:() =>import('../views/login/changePwd.vue'),
                meta: { auth: true,title: '修改密码'}

            },
            {
                //查看房间监控
                path:'/room',
                component:() =>import('../views/page/monitor/room.vue'),
                meta: { auth: true,title: '实时查看房间监控'}
            },


            {
                //查看工作人员列表
                path:'/workers',
                component:() =>import('../views/page/work/workers.vue'),
                meta: { auth: true,title: '查看工作人员列表'}
            },
            {
                //工作人员分析图
                path:'/workerImage',
                component:() =>import('../views/page/work/workerImage.vue'),
                meta: {auth: true, title: '工作人员分析图' }
            },
            {
                //日程表
                path:'/calendar',
                component:() =>import('../views/page/schedule/calendar.vue'),
                meta: { auth: true,title: '日程表' }
            },
            {
                //交互信息
                path:'/interactions',
                component:() =>import('../views/page/record/interactions.vue'),
                meta: {auth: true, title: '交互信息' }
            },
            {
                //异常信息
                path:'/abnormal',
                component:() =>import('../views/page/record/abnormal.vue'),
                meta: {auth: true, title: '异常信息' }
            }
        ]
    }
]

export default routes
